import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Action = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }
  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light gap-20">
      <section className="relative container p-8 grid grid-col-1">
        <img
          className="absolute left-40 top-10 shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <p>{userData.name}</p>
        <h1 className="text-center text-8xl">ACTIONS</h1>
      </section>
      <div class="mb-6">
        <label
          for="success"
          className="block mb-2 text-sm font-medium text-green-700 dark:text-green-500"
        >
          Your name
        </label>
        <input
          type="text"
          id="success"
          className="bg-green-50 border border-green-500 text-green-900 dark:text-green-400 placeholder-green-700 dark:placeholder-green-500 text-sm rounded-lg focus:ring-green-500 focus:border-green-500 block w-full p-2.5 dark:bg-gray-700 dark:border-green-500"
          placeholder="Success input"
        />
        <p class="mt-2 text-sm text-green-600 dark:text-green-500">
          <span className="font-medium">Well done!</span> Some success messsage.
        </p>
      </div>
      <div>
        <label
          for="error"
          className="block mb-2 text-sm font-medium text-red-700 dark:text-red-500"
        >
          Your name
        </label>
        <input
          type="text"
          id="error"
          className="bg-red-50 border border-red-500 text-red-900 placeholder-red-700 text-sm rounded-lg focus:ring-red-500 dark:bg-gray-700 focus:border-red-500 block w-full p-2.5 dark:text-red-500 dark:placeholder-red-500 dark:border-red-500"
          placeholder="Error input"
        />
        <p className="mt-2 text-sm text-red-600 dark:text-red-500">
          <span className="font-medium">Oh, snapp!</span> Some error message.
        </p>
      </div>
      <div>
        <button>OK</button>
      </div>
    </div>
  );
};

export default Action;
