import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Rate = () => {
  const { id } = useParams();
  let info = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i].id === id) {
      info = json[i];
      break;
    }
  }

  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light">
      <img
        className="shadow w-20 h-20 rounded-full object-cover"
        src={"../assets//" + userData.picture}
        alt=""
      />
      <h1 className="text-8xl">RATE</h1>
      <section className="container p-8 grid grid-cols-3">
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="" src="../assets/red.png" alt="lavage" />
        </div>
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="" src="../assets/orange.png" alt="sechage" />
        </div>
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="" src="../assets/green.png" alt="repassage" />
        </div>
      </section>
    </div>
  );
};

export default Rate;
