import { Link, useParams } from "react-router-dom";
import json from "../users.json";

function classNames(classes) {
  return classes.filter(Boolean).join(" ");
}

const Lavage = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }

  const sendData = (e) => {
    e.preventDefault();
    fetch("http://localhost:3000/data/error.json", {
      mode: "no-cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((result) => {
        let arr = Object.keys(result);
        return arr.includes("errors");
      });
  };

  let chupapi = false;

  return (
    <div
      className="w-3/4 flex justify-center items-center flex-col gap-5 bg-red-500"
      key="resultPan"
    >
      <div className="flex justify-center items-center gap-7">
        <img
          className="shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <p>{userData.name + " " + userData.lastName}</p>
      </div>
      <section className="relative container p-8">
        <h1 className="text-center text-6xl">LAVAGE</h1>
      </section>
      <div>
        <div>
          <div className="bg-white p-10 rounded-lg shadow w-96">
            <form action="">
              <div className="mb-5">
                <label className="block mb-2 font-bold text-gray-600">
                  CODE BAR
                </label>
                <input
                  type="text"
                  id="myInput"
                  className="border border-gray-300 shadow p-3 w-full rounded mb-"
                  autoFocus
                  onKeyDown={(e) => e.key === "Enter" && sendData(e)}
                />
              </div>

              <button className="block w-full bg-blue-500 text-white font-bold p-4 rounded-lg">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Lavage;
