import React from "react";
import { useParams } from "react-router-dom";
import ReactPlayer from "react-player";
import json from "../users.json";

const Modal = ({ open }) => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }

  const url = new URLSearchParams(window.location.pathname)
    .toString()
    .split("=%2F");
  console.log(url);

  if (!open) return null;
  return (
    <div className="overlay">
      <div className="modalContainer flex justify-center items-center">
        <ReactPlayer
          url={userData.ateliers[1].video}
          className="react-player fixed top-40% left-50%"
          playing
          width="50%"
          height="50%"
          controls={false}
        />
      </div>
    </div>
  );
};

export default Modal;
