import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faVideo } from "@fortawesome/free-solid-svg-icons";
import { Link, useParams } from "react-router-dom";
import ReactPlayer from "react-player";
import json from "../users.json";
import Modal from "../components/Modal";

const Lavage = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }

  const sendData = (e) => {
    e.preventDefault();
    fetch("http://localhost:3000/data/data.json", {
      mode: "no-cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((result) => {
        let arr = Object.keys(result);
        const el = document.getElementById("screen");
        if (arr.includes("errors")) {
          el.className =
            "w-screen min-h-screen flex justify-center items-center bg-red-500 flex-col space-y-28 scroll-smooth";
          setTimeout(() => {
            el.className =
              "w-screen min-h-screen flex justify-center items-center bg-igray flex-col space-y-28 scroll-smooth";
          }, 250);
        } else {
          el.className =
            "w-screen min-h-screen flex justify-center items-center bg-emerald-500 flex-col space-y-28 scroll-smooth";
          setTimeout(() => {
            el.className =
              "w-screen min-h-screen flex justify-center items-center bg-igray flex-col space-y-28 scroll-smooth";
          }, 250);
        }
      });
  };

  return (
    <div className="" id="resultPanel">
      <div className="flex justify-center items-center gap-7">
        <img
          className="shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <p>{userData.name + " " + userData.lastName}</p>
      </div>
      <section className="container p-8 grid grid-cols-1">
        <h1 className="text-center text-6xl">LAVAGE</h1>
        <p className="text-center">{userData.ateliers[1].instructions}</p>
        <div className="text-center p-3">
          <button
            class="block text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
            type="button"
            data-modal-toggle="defaultModal"
          >
            Toggle modal
          </button>

          <div
            id="defaultModal"
            tabindex="-1"
            aria-hidden="true"
            class="hidden overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 w-full md:inset-0 h-modal md:h-full justify-center items-center"
          >
            <div class="relative p-4 w-full max-w-2xl h-full md:h-auto">
              <div class="relative bg-white rounded-lg shadow dark:bg-gray-700">
                <div class="flex justify-between items-start p-4 rounded-t border-b dark:border-gray-600">
                  <h3 class="text-xl font-semibold text-gray-900 dark:text-white">
                    Terms of Service
                  </h3>
                  <button
                    type="button"
                    class="text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm p-1.5 ml-auto inline-flex items-center dark:hover:bg-gray-600 dark:hover:text-white"
                    data-modal-toggle="defaultModal"
                  >
                    <svg
                      class="w-5 h-5"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        fill-rule="evenodd"
                        d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                        clip-rule="evenodd"
                      ></path>
                    </svg>
                  </button>
                </div>
                <div class="p-6 space-y-6">
                  <p class="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    With less than a month to go before the European Union
                    enacts new consumer privacy laws for its citizens, companies
                    around the world are updating their terms of service
                    agreements to comply.
                  </p>
                  <p class="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    The European Union’s General Data Protection Regulation
                    (G.D.P.R.) goes into effect on May 25 and is meant to ensure
                    a common set of data rights in the European Union. It
                    requires organizations to notify users as soon as possible
                    of high-risk data breaches that could personally affect
                    them.
                  </p>
                </div>
                <div class="flex items-center p-6 space-x-2 rounded-b border-t border-gray-200 dark:border-gray-600">
                  <button
                    data-modal-toggle="defaultModal"
                    type="button"
                    class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
                  >
                    I accept
                  </button>
                  <button
                    data-modal-toggle="defaultModal"
                    type="button"
                    class="text-gray-500 bg-white hover:bg-gray-100 focus:ring-4 focus:outline-none focus:ring-blue-300 rounded-lg border border-gray-200 text-sm font-medium px-5 py-2.5 hover:text-gray-900 focus:z-10 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-500 dark:hover:text-white dark:hover:bg-gray-600 dark:focus:ring-gray-600"
                  >
                    Decline
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <div>
        <div>
          <div className="bg-white p-10 rounded-lg shadow w-96">
            <form action="">
              <div className="mb-5">
                <label className="block mb-2 font-bold text-gray-600">
                  CODE BAR
                </label>
                <input
                  type="text"
                  id="myInput"
                  className="border border-gray-300 shadow p-3 w-full rounded mb-"
                  autoFocus
                  onKeyDown={(e) => e.key === "Enter" && sendData(e)}
                />
              </div>
              <Link to={"../rate/1/" + userData.id}>
                <button className="block w-full bg-orange-500 text-white font-bold p-4 rounded-lg">
                  QUITTER
                </button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Lavage;
