import { Link, useParams } from "react-router-dom";
import json from "../users.json";

import { useState } from "react";
import { useEffect } from "react";
import Modal from "../components/Modal";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faVideo } from "@fortawesome/free-solid-svg-icons";

const Repassage = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }

  const [openModal, setOpenModal] = useState(false);
  const el = document.getElementById("screen");

  useEffect(() => {
    document.addEventListener("mousedown", () => {
      setOpenModal(false);
    });
  });

  const sendData = (e) => {
    e.preventDefault();
    fetch("http://localhost:3000/data/data.json", {
      mode: "no-cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((result) => {
        let arr = Object.keys(result);
        if (arr.includes("errors")) {
          el.className =
            "w-screen min-h-screen flex justify-center items-center bg-red-500 flex-col space-y-28 scroll-smooth";
          setTimeout(() => {
            el.className =
              "w-screen min-h-screen flex justify-center items-center bg-igray flex-col space-y-28 scroll-smooth";
          }, 250);
        } else {
          el.className =
            "w-screen min-h-screen flex justify-center items-center bg-emerald-500 flex-col space-y-28 scroll-smooth";
          setTimeout(() => {
            el.className =
              "w-screen min-h-screen flex justify-center items-center bg-igray flex-col space-y-28 scroll-smooth";
          }, 250);
        }
      });
  };

  if (openModal) {
    el.className =
      "w-screen min-h-screen flex justify-center items-center bg-igray flex-col space-y-28 scroll-smooth sm-blur";
  }

  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light gap-5">
      <div className="flex justify-center items-center gap-7">
        <img
          className="shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <p>{userData.name + " " + userData.lastName}</p>
      </div>
      <section className="container p-8 grid grid-cols-1">
        <h1 className="text-center text-6xl">REPASSAGE</h1>
        <p className="text-center">{userData.ateliers[3].instructions}</p>
        <div className="text-center p-3">
          <FontAwesomeIcon
            className="fa-solid fa-user text-[70px] text-indigo-600 hover:text-amber-400"
            onClick={() => setOpenModal(true)}
            icon={faVideo}
          />
          <Modal open={openModal} />
        </div>
      </section>
      <div>
        <div>
          <div className="bg-white p-10 rounded-lg shadow w-96">
            <form action="">
              <div className="mb-5">
                <label className="block mb-2 font-bold text-gray-600">
                  CODE BAR
                </label>
                <input
                  type="text"
                  id="myInput"
                  className="border border-gray-300 shadow p-3 w-full rounded mb-"
                  autoFocus
                  onKeyDown={(e) => e.key === "Enter" && sendData(e)}
                />
              </div>

              <Link to={"../rate/1/" + userData.id}>
                <button className="block w-full bg-orange-500 text-white font-bold p-4 rounded-lg">
                  QUITTER
                </button>
              </Link>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Repassage;
