import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Action = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }
  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light gap-48">
      <section className="relative container p-8">
        <img
          className="absolute left-40 top-10 shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <h1 className="text-center text-8xl">ACTIONS</h1>
      </section>
      <div className="">
        <input className="w-80"></input>
      </div>
      <div>
        <button>OK</button>
      </div>
    </div>
  );
};

export default Action;
