import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Rate = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }
  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light">
      <img
        className="shadow w-20 h-20 rounded-full object-cover"
        src={"/assets//" + userData.picture}
        alt=""
      />
      <h1 className="text-8xl">Rate</h1>
      <section className="container p-8 min-w-large mt-32 grid grid-cols-3 place-items-center">
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="h-36 w-36" src="/assets/red.png" alt="red" />
        </div>
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="h-36 w-36" src="/assets/orange.png" alt="orange" />
        </div>
        <div className="m-5 rounded-lg overflow-hidden">
          <img className="h-36 w-36" src="/assets/green.png" alt="green" />
        </div>
      </section>
    </div>
  );
};

export default Rate;
