import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Repassage = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }
  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light gap-20">
      <section className="relative container p-8">
        <h1 className="text-center text-8xl">REPASSAGE</h1>
      </section>
      <div>
        <div>
          <div class="bg-white p-10 rounded-lg shadow w-96">
            <form action="">
              <div className="mb-5">
                <label
                  for="name"
                  className="block mb-2 font-bold text-gray-600"
                >
                  CODE BAR
                </label>
                <input
                  type="text"
                  id="codebar"
                  name="name"
                  placeholder="...."
                  className="border border-gray-300 shadow p-3 w-full rounded mb-"
                />
              </div>

              <button className="block w-full bg-blue-500 text-white font-bold p-4 rounded-lg">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Repassage;
