import { Link, useParams } from "react-router-dom";
import json from "../users.json";

const Ateliers = () => {
  const { id } = useParams();
  let userData = null;
  for (let i = 0; i < json.length; i++) {
    if (json[i]["id"] == id) {
      userData = json[i];
      break;
    }
  }
  return (
    <div className="w-3/4 flex justify-center items-center flex-col bg-igray-light">
      <div className="flex justify-center items-center m-10 gap-7 grid grid-cols-3">
        <img
          className="shadow w-20 h-20 rounded-full object-cover"
          src={"/assets/" + userData.picture}
          alt={userData.group}
        />
        <p className=" text-4xl">
          {"Bonjour, " + userData.name + " " + userData.lastName}
        </p>
      </div>
      <section className="relative container p-8">
        <h1 className="text-center text-8xl">ATELIERS</h1>
      </section>
      <section className="container p-8 grid grid-cols-3">
        <Link to={"/ateliers/1/" + userData.id}>
          <div className="m-5 shadow-lg rounded-3xl overflow-hidden">
            <img className="" src="../assets/lavage.jpg" alt="lavage" />
            <div className="p-8">
              <h2 className="text-blue-800 text-xl my-3">LAVAGE</h2>
              <p className="text-gray-800 text-sm">
                {userData.ateliers[1].description}
              </p>
            </div>
          </div>
        </Link>
        <Link to={"/ateliers/2/" + userData.id}>
          <div className="m-5 shadow-lg rounded-3xl overflow-hidden">
            <img className="" src="../assets/sechage.jpg" alt="sechage" />
            <div className="p-8">
              <h2 className="text-blue-800 text-xl my-3">SECHAGE</h2>
              <p className="text-gray-800 text-sm">
                {userData.ateliers[2].description}
              </p>
            </div>
          </div>
        </Link>
        <Link to={"/ateliers/3/" + userData.id}>
          <div className="m-5 shadow-lg rounded-3xl overflow-hidden">
            <img className="" src="../assets/repassage.jpg" alt="repassage" />
            <div className="p-8">
              <h2 className="text-blue-800 text-xl my-3">REPASSAGE</h2>
              <p className="text-gray-800 text-sm">
                {userData.ateliers[3].description}
              </p>
            </div>
          </div>
        </Link>
      </section>
    </div>
  );
};

export default Ateliers;
